        <i class="message-icon">‚ö†</i>
        {{ error }}
      </div>

      <div v-if="successMessage" class="message success-message">
        <i class="message-icon">‚úÖ</i>
        {{ successMessage }}
      </div>
    </div>
  </div>
</template>

<script>
export default {
  name: 'ForgotPasswordAuthComponent',
  data() {
    return {
      currentView: 'login', // 'login' o 'register'
      loginData: {
        email: '',
        password: ''
      },
      registerData: {
        name: '',
        email: '',
        password: '',
        confirmPassword: '',
        phone: ''
      },
      adminLoginData: {
        email: '',
        password: ''
      },
      rememberMe: false,
      acceptTerms: false,
      isLoading: false,
      error: '',
      successMessage: '',

      // Sistema de recuperaci√≥n de contrase√±a
      showForgotPasswordModal: false,
      currentStep: 'email', // 'email', 'code', 'reset'
      isProcessing: false,
      modalError: '',
      modalSuccess: '',
      
      forgotPasswordData: {
        email: '',
        newPassword: '',
        confirmPassword: ''
      },
      
      // C√≥digo de verificaci√≥n
      verificationCode: ['', '', '', '', '', ''],
      generatedCode: '',
      codeTimer: 0,
      timerInterval: null,
      
      // Validaci√≥n de contrase√±a
      passwordStrength: 0,
      showNewPassword: false,
      showConfirmPassword: false
    }
  },

  computed: {
    maskedEmail() {
      if (!this.forgotPasswordData.email) return '';
      const [username, domain] = this.forgotPasswordData.email.split('@');
      const maskedUsername = username.substring(0, 2) + '*'.repeat(username.length - 2);
      return `${maskedUsername}@${domain}`;
    },

    isCodeComplete() {
      return this.verificationCode.every(digit => digit !== '');
    },

    passwordsMatch() {
      return this.forgotPasswordData.newPassword === this.forgotPasswordData.confirmPassword;
    },

    passwordStrengthClass() {
      if (this.passwordStrength <= 1) return 'weak';
      if (this.passwordStrength <= 2) return 'fair';
      if (this.passwordStrength <= 3) return 'good';
      return 'strong';
    },

    passwordStrengthWidth() {
      return (this.passwordStrength / 4) * 100;
    },

    passwordStrengthText() {
      const texts = ['Muy D√©bil', 'D√©bil', 'Regular', 'Buena', 'Muy Fuerte'];
      return texts[this.passwordStrength] || 'Muy D√©bil';
    },

    isResetFormValid() {
      return (
        this.forgotPasswordData.newPassword &&
        this.forgotPasswordData.confirmPassword &&
        this.passwordsMatch &&
        this.passwordStrength >= 3
      );
    }
  },

  methods: {
  // M√©todo para ir al inicio
    goHome() {
      if (this.$router) {
        this.$router.push('/');
      } else {
        window.location.href = '/';
      }
    },

    // M√©todo para ir al panel de administrador
    goToAdminPanel() {
      // Cambiar a vista de admin
      this.currentView = 'admin';
      this.clearMessages();
      this.resetAdminData();
    },
    backToUserLogin() {
      this.currentView = 'login';
      this.clearMessages();
      this.resetForms();
    },
    switchView(view) {
      this.currentView = view;
      this.clearMessages();
      this.resetForms();
    },
    clearMessages() {
      this.error = '';
      this.successMessage = '';
    },

    
    // ==== M√âTODOS DE LOGIN DE ADMINISTRADOR ====

    // Resetear datos de admin
    resetAdminData() {
      this.adminLoginData = {
        email: '',
        password: '',
        accessCode: ''
      };
      this.showAdminPassword = false;
    },

    // Alternar visibilidad de contrase√±a de admin
    toggleAdminPasswordVisibility() {
      this.showAdminPassword = !this.showAdminPassword;
    },

    // Verificar credenciales de administrador
    verifyAdminCredentials(email, password, accessCode = '') {
      // Lista de administradores predefinidos
      const adminCredentials = [
        {
          email: 'admin@admin.com',
          password: 'admin123',
          name: 'Administrador Principal',
          level: 'super',
          accessCode: 'MASTER2024'
        },
        {
          email: 'administrador@sistema.com',
          password: 'seguro456',
          name: 'Administrador del Sistema',
          level: 'system',
          accessCode: 'SYS2024'
        },
        {
          email: 'admin@empresa.com',
          password: 'empresa789',
          name: 'Administrador Empresarial',
          level: 'business',
          accessCode: 'BIZ2024'
        }
      ];

      // Buscar admin por email y password
      const admin = adminCredentials.find(a => 
        a.email.toLowerCase() === email.toLowerCase() && 
        a.password === password
      );

      if (!admin) return null;

      // Verificar c√≥digo de acceso si se proporciona
      if (accessCode && accessCode !== admin.accessCode) {
        return { ...admin, codeValid: false };
      }

      return { ...admin, codeValid: true };
    },

    // Manejar login de administrador
    async handleAdminLogin() {
      this.isAdminProcessing = true;
      this.clearMessages();

      try {
        // Validaciones b√°sicas
        if (!this.adminLoginData.email || !this.adminLoginData.password) {
          throw new Error('Por favor completa todos los campos requeridos');
        }

        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailRegex.test(this.adminLoginData.email)) {
          throw new Error('Por favor ingresa un email v√°lido');
        }

        // Simular delay de verificaci√≥n
        await new Promise(resolve => setTimeout(resolve, 2000));

        // Verificar credenciales
        const adminUser = this.verifyAdminCredentials(
          this.adminLoginData.email,
          this.adminLoginData.password,
          this.adminLoginData.accessCode
        );

        if (!adminUser) {
          throw new Error('Credenciales de administrador incorrectas');
        }

        // Verificar c√≥digo de acceso si se proporcion√≥
        if (this.adminLoginData.accessCode && !adminUser.codeValid) {
          throw new Error('C√≥digo de acceso inv√°lido');
        }

        // Generar token de admin
        const adminToken = 'admin-jwt-' + this.generateUserId();
        
        // Guardar sesi√≥n de admin
        localStorage.setItem('adminToken', adminToken);
        localStorage.setItem('adminUser', JSON.stringify({
          id: adminUser.email,
          email: adminUser.email,
          name: adminUser.name,
          level: adminUser.level,
          loginTime: new Date().toISOString()
        }));

        this.successMessage = `¬°Bienvenido, ${adminUser.name}! Accediendo al panel...`;
        
        // Redirigir al panel despu√©s de 2 segundos
        setTimeout(() => {
          if (this.$router) {
            this.$router.push('/admin');
          } else {
            window.location.href = '/admin';
          }
        }, 2000);

      } catch (error) {
        this.error = error.message;
      } finally {
        this.isAdminProcessing = false;
      }
    },
  
    // ==== M√âTODOS DE RECUPERACI√ìN DE CONTRASE√ëA ====

    // Abrir modal de recuperaci√≥n
    openForgotPasswordModal() {
      this.showForgotPasswordModal = true;
      this.currentStep = 'email';
      this.clearModalMessages();
      this.resetForgotPasswordData();
    },

    // Cerrar modal de recuperaci√≥n
    closeForgotPasswordModal() {
      this.showForgotPasswordModal = false;
      this.clearModalMessages();
      this.resetForgotPasswordData();
      this.stopTimer();
    },

    // Limpiar mensajes del modal
    clearModalMessages() {
      this.modalError = '';
      this.modalSuccess = '';
    },

    // Resetear datos de recuperaci√≥n
    resetForgotPasswordData() {
      this.forgotPasswordData = {
        email: '',
        newPassword: '',
        confirmPassword: ''
      };
      this.verificationCode = ['', '', '', '', '', ''];
      this.generatedCode = '';
      this.passwordStrength = 0;
    },

    // Generar c√≥digo de verificaci√≥n
    generateVerificationCode() {
      return Math.floor(100000 + Math.random() * 900000).toString();
    },

    // Obtener usuarios registrados
    getRegisteredUsers() {
      try {
        const users = localStorage.getItem('registeredUsers');
        return users ? JSON.parse(users) : [];
      } catch (error) {
        console.error('Error al obtener usuarios:', error);
        return [];
      }
    },

    // Verificar si existe un usuario con el email
    findUserByEmail(email) {
      const users = this.getRegisteredUsers();
      return users.find(user => user.email.toLowerCase() === email.toLowerCase());
    },

    // Enviar c√≥digo de restablecimiento
    async sendResetCode() {
      this.isProcessing = true;
      this.clearModalMessages();

      try {
        // Simular delay
        await new Promise(resolve => setTimeout(resolve, 1500));

        // Verificar si el email existe
        const user = this.findUserByEmail(this.forgotPasswordData.email);
        if (!user) {
          throw new Error('No encontramos una cuenta asociada a este email');
        }

        // Generar c√≥digo
        this.generatedCode = this.generateVerificationCode();
        
        // Simular env√≠o de email
        console.log(`C√≥digo de verificaci√≥n enviado a ${this.forgotPasswordData.email}: ${this.generatedCode}`);
        
        // Iniciar timer de 10 minutos
        this.codeTimer = 600;
        this.startTimer();

        this.modalSuccess = 'C√≥digo enviado exitosamente. Revisa tu email.';
        
        // Cambiar al siguiente paso despu√©s de 2 segundos
        setTimeout(() => {
          this.currentStep = 'code';
          this.clearModalMessages();
        }, 2000);

      } catch (error) {
        this.modalError = error.message;
      } finally {
        this.isProcessing = false;
      }
    },

    // Iniciar timer del c√≥digo
    startTimer() {
      if (this.timerInterval) {
        clearInterval(this.timerInterval);
      }
      
      this.timerInterval = setInterval(() => {
        this.codeTimer--;
        if (this.codeTimer <= 0) {
          this.stopTimer();
        }
      }, 1000);
    },

    // Detener timer del c√≥digo
    stopTimer() {
      if (this.timerInterval) {
        clearInterval(this.timerInterval);
        this.timerInterval = null;
      }
    },

    // Reenviar c√≥digo
    async resendCode() {
      this.isProcessing = true;
      this.clearModalMessages();

      try {
        await new Promise(resolve => setTimeout(resolve, 1000));

        // Generar nuevo c√≥digo
        this.generatedCode = this.generateVerificationCode();
        
        console.log(`Nuevo c√≥digo enviado a ${this.forgotPasswordData.email}: ${this.generatedCode}`);
        
        // Reiniciar timer
        this.codeTimer = 600;
        this.startTimer();

        this.modalSuccess = 'Nuevo c√≥digo enviado exitosamente.';
        setTimeout(() => this.clearModalMessages(), 3000);

      } catch (error) {
        this.modalError = 'Error al reenviar el c√≥digo. Int√©ntalo de nuevo.';
      } finally {
        this.isProcessing = false;
      }
    },

    // Manejar entrada de c√≥digo
    handleCodeInput(index, event) {
      const value = event.target.value;
      
      if (value && /^\d$/.test(value)) {
        this.verificationCode[index] = value;
        
        // Mover al siguiente campo
        if (index < 5) {
          const nextInput = this.$refs[`codeInput${index + 1}`][0];
          if (nextInput) {
            nextInput.focus();
          }
        }
      } else if (!value) {
        this.verificationCode[index] = '';
      }
    },

    // Manejar teclas en c√≥digo
    handleCodeKeydown(index, event) {
      if (event.key === 'Backspace' && !this.verificationCode[index] && index > 0) {
        const prevInput = this.$refs[`codeInput${index - 1}`][0];
        if (prevInput) {
          prevInput.focus();
        }
      } else if (event.key === 'ArrowLeft' && index > 0) {
        const prevInput = this.$refs[`codeInput${index - 1}`][0];
        if (prevInput) {
          prevInput.focus();
        }
      } else if (event.key === 'ArrowRight' && index < 5) {
        const nextInput = this.$refs[`codeInput${index + 1}`][0];
        if (nextInput) {
          nextInput.focus();
        }
      }
    },

    // Manejar pegado de c√≥digo
    handleCodePaste(event) {
      event.preventDefault();
      const pastedData = event.clipboardData.getData('text').slice(0, 6);
      
      if (/^\d{6}$/.test(pastedData)) {
        for (let i = 0; i < 6; i++) {
          this.verificationCode[i] = pastedData[i];
        }
      }
    },

    // Verificar c√≥digo de restablecimiento
    async verifyResetCode() {
      this.isProcessing = true;
      this.clearModalMessages();

      try {
        await new Promise(resolve => setTimeout(resolve, 1000));

        const enteredCode = this.verificationCode.join('');
        
        if (enteredCode !== this.generatedCode) {
          throw new Error('C√≥digo de verificaci√≥n incorrecto');
        }

        if (this.codeTimer <= 0) {
          throw new Error('El c√≥digo ha expirado. Solicita uno nuevo.');
        }

        this.modalSuccess = '¬°C√≥digo verificado correctamente!';
        
        setTimeout(() => {
          this.currentStep = 'reset';
          this.clearModalMessages();
        }, 1500);

      } catch (error) {
        this.modalError = error.message;
      } finally {
        this.isProcessing = false;
      }
    },

    // Alternar visibilidad de contrase√±as
    toggleNewPasswordVisibility() {
      this.showNewPassword = !this.showNewPassword;
    },

    toggleConfirmPasswordVisibility() {
      this.showConfirmPassword = !this.showConfirmPassword;
    },

    // Validar fortaleza de la nueva contrase√±a
    validateNewPassword() {
      const password = this.forgotPasswordData.newPassword;
      
      // Calcular fortaleza basada en criterios
      let strength = 0;
      if (password.length >= 8) strength++;
      if (/[A-Z]/.test(password)) strength++;
      if (/[a-z]/.test(password)) strength++;
      if (/\d/.test(password)) strength++;
      if (/[!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?]/.test(password)) strength++;

      this.passwordStrength = strength;
    },

    // Guardar usuarios actualizados
    saveUsers(users) {
      try {
        localStorage.setItem('registeredUsers', JSON.stringify(users));
        return true;
      } catch (error) {
        console.error('Error al guardar usuarios:', error);
        return false;
      }
    },

    // Restablecer contrase√±a
    async resetPassword() {
      this.isProcessing = true;
      this.clearModalMessages();

      try {
        // Validaciones
        if (!this.forgotPasswordData.newPassword || !this.forgotPasswordData.confirmPassword) {
          throw new Error('Por favor completa todos los campos');
        }

        if (this.forgotPasswordData.newPassword.length < 8) {
          throw new Error('La contrase√±a debe tener al menos 8 caracteres');
        }

        if (!this.passwordsMatch) {
          throw new Error('Las contrase√±as no coinciden');
        }

        if (this.passwordStrength < 3) {
          throw new Error('La contrase√±a no es lo suficientemente fuerte');
        }

        await new Promise(resolve => setTimeout(resolve, 2000));

        // Buscar y actualizar usuario
        const users = this.getRegisteredUsers();
        const userIndex = users.findIndex(user => 
          user.email.toLowerCase() === this.forgotPasswordData.email.toLowerCase()
        );

        if (userIndex === -1) {
          throw new Error('Usuario no encontrado');
        }

        // Actualizar contrase√±a
        users[userIndex].password = this.forgotPasswordData.newPassword;
        users[userIndex].lastPasswordReset = new Date().toISOString();

        const saved = this.saveUsers(users);
        if (!saved) {
          throw new Error('Error al guardar la nueva contrase√±a');
        }

        // Detener timer
        this.stopTimer();

        this.modalSuccess = '¬°Contrase√±a restablecida exitosamente! Ya puedes iniciar sesi√≥n.';
        
        // Cerrar modal y rellenar email en login despu√©s de 3 segundos
        setTimeout(() => {
          this.loginData.email = this.forgotPasswordData.email;
          this.closeForgotPasswordModal();
        }, 3000);

      } catch (error) {
        this.modalError = error.message;
      } finally {
        this.isProcessing = false;
      }
    },

    // ==== M√âTODOS ORIGINALES DE AUTENTICACI√ìN ====

    switchView(view) {
      this.currentView = view;
      this.clearMessages();
      this.resetForms();
    },

    clearMessages() {
      this.error = '';
      this.successMessage = '';
    },

    resetForms() {
      this.loginData = { email: '', password: '' };
      this.registerData = {
        name: '',
        email: '',
        password: '',
        confirmPassword: '',
        phone: ''
      };
      this.acceptTerms = false;
      this.rememberMe = false;
    },

    getExistingUsers() {
      const users = localStorage.getItem('registeredUsers');
      return users ? JSON.parse(users) : [];
    },

    generateUserId() {
      return Date.now().toString(36) + Math.random().toString(36).substr(2);
    },

    async handleRegister() {
      this.isLoading = true;
      this.clearMessages();
      
      try {
        if (!this.registerData.name.trim()) {
          throw new Error('El nombre es requerido');
        }

        if (!this.registerData.email || !this.registerData.password) {
          throw new Error('Por favor completa todos los campos obligatorios');
        }

        if (this.registerData.password.length < 6) {
          throw new Error('La contrase√±a debe tener al menos 6 caracteres');
        }

        if (this.registerData.password !== this.registerData.confirmPassword) {
          throw new Error('Las contrase√±as no coinciden');
        }

        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailRegex.test(this.registerData.email)) {
          throw new Error('Por favor ingresa un email v√°lido');
        }

        await new Promise(resolve => setTimeout(resolve, 1500));

        const existingUsers = this.getExistingUsers();
        const emailExists = existingUsers.some(user => 
          user.email.toLowerCase() === this.registerData.email.toLowerCase()
        );

        if (emailExists) {
          throw new Error('Este email ya est√° registrado');
        }

        const newUser = {
          id: this.generateUserId(),
          name: this.registerData.name.trim(),
          email: this.registerData.email.toLowerCase(),
          password: this.registerData.password,
          phone: this.registerData.phone.trim() || null,
          createdAt: new Date().toISOString(),
          isActive: true
        };

        existingUsers.push(newUser);
        this.saveUsers(existingUsers);

        this.successMessage = '¬°Cuenta creada exitosamente! Ahora puedes iniciar sesi√≥n.';
        
        setTimeout(() => {
          this.switchView('login');
          this.loginData.email = newUser.email;
        }, 2000);
        
      } catch (err) {
        this.error = err.message || 'Error al crear la cuenta. Int√©ntalo de nuevo.';
      } finally {
        this.isLoading = false;
      }
    },

    async handleLogin() {
      this.isLoading = true;
      this.clearMessages();
      
      try {
        if (!this.loginData.email || !this.loginData.password) {
          throw new Error('Por favor completa todos los campos');
        }

        await new Promise(resolve => setTimeout(resolve, 1500));
        
        const registeredUsers = this.getExistingUsers();
        const user = registeredUsers.find(u => 
          u.email.toLowerCase() === this.loginData.email.toLowerCase() && 
          u.password === this.loginData.password &&
          u.isActive
        );

        if (user) {
          const authToken = 'jwt-' + this.generateUserId();
          localStorage.setItem('authToken', authToken);
          localStorage.setItem('user', JSON.stringify({
            id: user.id,
            email: user.email,
            name: user.name,
            phone: user.phone
          }));

          this.successMessage = `¬°Bienvenido, ${user.name}!`;
          
          setTimeout(() => {
            if (this.$router) {
              this.$router.push('/');
            } else {
              window.location.href = '/';
            }
          }, 1000);
          
        } else {
          throw new Error('Credenciales incorrectas o cuenta inactiva');
        }
        
      } catch (err) {
        this.error = err.message || 'Error en la autenticaci√≥n. Verifica tus credenciales.';
      } finally {
        this.isLoading = false;
      }
    }
  },

  // Limpiar timer al destruir el componente
  beforeDestroy() {
    this.stopTimer();
  },
  
 
}
</script>

<style scoped>
