    0 12px 35px rgba(255, 215, 0, 0.4),
    inset 0 1px 0 rgba(255, 215, 0, 0.2);
}

.nav-btn:hover svg {
  transform: scale(1.1);
  filter: drop-shadow(0 0 8px rgba(255, 215, 0, 0.6));
}

/* =================================== */
/* ESTILOS DEL MODAL DE RECUPERACI√ìN   */
/* =================================== */

.modal-overlay {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.85);
  backdrop-filter: blur(15px);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 2000;
  animation: fadeIn 0.3s ease-out;
}

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

.modal-card {
  background: rgba(20, 20, 20, 0.95);
  backdrop-filter: blur(25px);
  border: 1px solid rgba(255, 215, 0, 0.3);
  border-radius: 20px;
  max-width: 550px;
  width: 90%;
  max-height: 90vh;
  overflow-y: auto;
  box-shadow: 
    0 30px 60px rgba(0, 0, 0, 0.8),
    inset 0 1px 0 rgba(255, 215, 0, 0.2),
    0 0 0 1px rgba(255, 215, 0, 0.1);
  animation: slideIn 0.3s ease-out;
  position: relative;
}

@keyframes slideIn {
  from { 
    opacity: 0;
    transform: translateY(-20px) scale(0.95);
  }
  to { 
    opacity: 1;
    transform: translateY(0) scale(1);
  }
}

.modal-card::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 4px;
  background: linear-gradient(90deg, #FFD700, #FF4500, #8B4513, #FFD700);
  background-size: 400% 400%;
  animation: shimmer 3s ease-in-out infinite;
  border-radius: 20px 20px 0 0;
}

@keyframes shimmer {
  0%, 100% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
}

.modal-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 2rem 2rem 1rem;
  border-bottom: 1px solid rgba(255, 215, 0, 0.1);
}

.modal-header h3 {
  margin: 0;
  color: #FFD700;
  font-size: 1.6rem;
  font-weight: 700;
  text-shadow: 0 0 15px rgba(255, 215, 0, 0.5);
  letter-spacing: 0.5px;
}

.close-btn {
  background: transparent;
  border: 1px solid rgba(255, 69, 0, 0.3);
  color: #FF4500;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0;
}

.close-btn svg {
  width: 20px;
  height: 20px;
  stroke: currentColor;
}

.close-btn:hover {
  background: rgba(255, 69, 0, 0.1);
  border-color: #FF4500;
  transform: scale(1.1);
  box-shadow: 0 0 15px rgba(255, 69, 0, 0.4);
}

.modal-content {
  padding: 2rem;
}

.modal-info {
  text-align: center;
  margin-bottom: 2rem;
}

.info-icon {
  font-size: 3.5rem;
  margin-bottom: 1rem;
  filter: drop-shadow(0 0 15px rgba(255, 215, 0, 0.6));
  animation: pulse-glow 2s ease-in-out infinite;
}

@keyframes pulse-glow {
  0%, 100% { 
    filter: drop-shadow(0 0 15px rgba(255, 215, 0, 0.6));
    transform: scale(1);
  }
  50% { 
    filter: drop-shadow(0 0 25px rgba(255, 215, 0, 0.8));
    transform: scale(1.05);
  }
}

.modal-info p {
  color: rgba(255, 255, 255, 0.8);
  font-size: 1rem;
  line-height: 1.5;
  margin: 0.5rem 0;
}

.email-info {
  font-size: 0.9rem;
  color: rgba(255, 255, 255, 0.6);
  font-style: italic;
}

.modal-form {
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
}

.form-group {
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
}

.form-group label {
  color: #FFD700;
  font-weight: 600;
  font-size: 0.9rem;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.form-group input {
  padding: 1rem 1.2rem;
  border: 1px solid rgba(255, 215, 0, 0.3);
  border-radius: 10px;
  background: rgba(0, 0, 0, 0.4);
  color: #ffffff;
  font-size: 1rem;
  transition: all 0.3s ease;
  backdrop-filter: blur(10px);
}

.form-group input:focus {
  outline: none;
  border-color: #FFD700;
  background: rgba(0, 0, 0, 0.6);
  box-shadow: 
    0 0 0 3px rgba(255, 215, 0, 0.2),
    inset 0 0 15px rgba(255, 215, 0, 0.1);
}

.form-group input::placeholder {
  color: rgba(255, 255, 255, 0.4);
  font-style: italic;
}

/* Entrada de c√≥digo de verificaci√≥n */
.code-input-container {
  display: flex;
  gap: 0.8rem;
  justify-content: center;
  margin: 1rem 0;
}

.code-digit {
  width: 50px !important;
  height: 60px;
  text-align: center;
  font-size: 1.5rem;
  font-weight: 700;
  border: 2px solid rgba(255, 215, 0, 0.3) !important;
  border-radius: 12px !important;
  background: rgba(0, 0, 0, 0.6) !important;
  color: #FFD700 !important;
  padding: 0 !important;
  transition: all 0.3s ease;
}

/* Timer del c√≥digo */
.code-timer {
  text-align: center;
  margin: 1rem 0;
}

.timer-text {
  color: #FFD700;
  font-size: 0.9rem;
  font-weight: 600;
}

.resend-btn {
  background: transparent;
  border: 1px solid rgba(255, 215, 0, 0.3);
  color: #FFD700;
  padding: 0.5rem 1rem;
  border-radius: 8px;
  cursor: pointer;
  transition: all 0.3s ease;
  font-size: 0.9rem;
}

.resend-btn:hover:not(:disabled) {
  background: rgba(255, 215, 0, 0.1);
  border-color: #FFD700;
}

/* Contenedor de contrase√±a con bot√≥n de mostrar/ocultar */
.password-input-container {
  position: relative;
  display: flex;
  align-items: center;
}

.password-input-container input {
  width: 100%;
  padding-right: 3rem !important;
}

.password-toggle-btn {
  position: absolute;
  right: 1rem;
  top: 50%;
  transform: translateY(-50%);
  background: transparent;
  border: none;
  color: rgba(255, 255, 255, 0.6);
  cursor: pointer;
  padding: 0.3rem;
  border-radius: 50%;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;
}

.password-toggle-btn:hover:not(:disabled) {
  color: #FFD700;
  background: rgba(255, 215, 0, 0.1);
  transform: translateY(-50%) scale(1.1);
}

.password-toggle-btn svg {
  width: 18px;
  height: 18px;
  stroke: currentColor;
}

/* Indicador de fortaleza de contrase√±a */
.password-strength {
  margin-top: 0.5rem;
  display: flex;
  align-items: center;
  gap: 1rem;
}

.strength-bar {
  flex: 1;
  height: 6px;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 3px;
  overflow: hidden;
}

.strength-fill {
  height: 100%;
  transition: all 0.3s ease;
  border-radius: 3px;
}

.strength-fill.weak {
  background: linear-gradient(90deg, #DC2626, #EF4444);
}

.strength-fill.fair {
  background: linear-gradient(90deg, #F59E0B, #FBBF24);
}

.strength-fill.good {
  background: linear-gradient(90deg, #10B981, #34D399);
}

.strength-fill.strong {
  background: linear-gradient(90deg, #059669, #10B981);
}

.strength-text {
  font-size: 0.8rem;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  min-width: 80px;
  text-align: center;
}

.strength-text.weak { color: #EF4444; }
.strength-text.fair { color: #FBBF24; }
.strength-text.good { color: #34D399; }
.strength-text.strong { color: #10B981; }

/* Indicador de coincidencia */
.password-match {
  margin-top: 0.5rem;
  font-size: 0.85rem;
  font-weight: 600;
}

.match-success {
  color: #34D399;
  display: flex;
  align-items: center;
  gap: 0.3rem;
}

.match-error {
  color: #EF4444;
  display: flex;
  align-items: center;
  gap: 0.3rem;
}

/* Botones del modal */
.modal-btn {
  padding: 1rem 1.5rem;
  border: none;
  border-radius: 10px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  position: relative;
  overflow: hidden;
}

.modal-btn.primary {
  background: linear-gradient(135deg, #FFD700, #FF4500);
  color: #000000;
  box-shadow: 0 4px 15px rgba(255, 215, 0, 0.3);
}

.modal-btn.primary:hover:not(:disabled) {
  transform: translateY(-2px);
  box-shadow: 0 8px 25px rgba(255, 215, 0, 0.5);
}

.modal-btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
  transform: none !important;
}

.spinner {
  width: 18px;
  height: 18px;
  border: 2px solid transparent;
  border-top: 2px solid currentColor;
  border-radius: 50%;
  animation: spin 1s linear infinite;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

/* ==================================== */
/* ESTILOS DEL COMPONENTE DE LOGIN      */
/* ==================================== */

.auth-card {
  background: rgba(20, 20, 20, 0.95);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 215, 0, 0.2);
  padding: 0;
  border-radius: 20px;
  box-shadow: 
    0 25px 50px rgba(0, 0, 0, 0.7),
    inset 0 1px 0 rgba(255, 215, 0, 0.1),
    0 0 0 1px rgba(255, 215, 0, 0.05);
  width: 100%;
  max-width: 450px;
  position: relative;
  overflow: hidden;
}

.auth-card::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 4px;
  background: linear-gradient(90deg, #FFD700, #FF4500, #8B4513, #FFD700);
  background-size: 400% 400%;
  animation: shimmer 3s ease-in-out infinite;
}

.auth-tabs {
  display: flex;
  background: rgba(0, 0, 0, 0.3);
  border-radius: 16px 16px 0 0;
  border-bottom: 1px solid rgba(255, 215, 0, 0.1);
}

.tab-btn {
  flex: 1;
  padding: 1.2rem;
  border: none;
  background: transparent;
