  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  border-bottom: 2px solid transparent;
  position: relative;
  font-size: 0.95rem;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.tab-btn.active {
  color: #FFD700;
  background: rgba(255, 215, 0, 0.1);
  border-bottom-color: #FFD700;
}

.tab-btn:hover:not(.active) {
  color: rgba(255, 255, 255, 0.8);
  background: rgba(255, 255, 255, 0.05);
}

.auth-view {
  padding: 2.5rem;
}

.auth-header {
  text-align: center;
  margin-bottom: 2rem;
}

.auth-header h2 {
  margin: 0 0 0.5rem 0;
  color: #FFD700;
  font-size: 2rem;
  font-weight: 700;
  text-shadow: 0 0 20px rgba(255, 215, 0, 0.5);
  letter-spacing: 1px;
}

.auth-header p {
  margin: 0;
  color: rgba(255, 255, 255, 0.7);
  font-size: 1rem;
  font-style: italic;
}

.auth-form {
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
}

.auth-form .form-group {
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
}

.auth-form .form-group label {
  color: #FFD700;
  font-weight: 600;
  font-size: 0.9rem;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  margin-bottom: 0.3rem;
}

.auth-form .form-group input {
  padding: 1rem 1.2rem;
  border: 1px solid rgba(255, 215, 0, 0.3);
  border-radius: 12px;
  font-size: 1rem;
  transition: all 0.3s ease;
  background: rgba(0, 0, 0, 0.4);
  color: #ffffff;
  backdrop-filter: blur(10px);
}

.auth-form .form-group input::placeholder {
  color: rgba(255, 255, 255, 0.4);
  font-style: italic;
}

.auth-form .form-group input:focus {
  outline: none;
  border-color: #FFD700;
  background: rgba(0, 0, 0, 0.6);
  box-shadow: 
    0 0 0 3px rgba(255, 215, 0, 0.2),
    inset 0 0 20px rgba(255, 215, 0, 0.1);
  transform: translateY(-1px);
}

.form-options {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-top: -0.5rem;
  gap: 1rem;
}

.checkbox-container {
  display: flex;
  align-items: center;
  cursor: pointer;
  font-size: 0.9rem;
  color: rgba(255, 255, 255, 0.8);
  line-height: 1.4;
}

.checkbox-container input[type="checkbox"] {
  margin-right: 0.5rem;
  width: auto;
  padding: 0;
  border: none;
  background: none;
  accent-color: #FFD700;
}

/* BOT√ìN MODIFICADO PARA "¬øOLVIDASTE TU CONTRASE√ëA?" */
.forgot-password-btn {
  background: transparent;
  border: none;
  color: #FFD700;
  text-decoration: none;
  font-size: 0.9rem;
  cursor: pointer;
  transition: all 0.3s ease;
  text-shadow: 0 0 10px rgba(255, 215, 0, 0.3);
  padding: 0;
  font-family: inherit;
}

.forgot-password-btn:hover {
  color: #FF4500;
  text-decoration: underline;
  text-shadow: 0 0 15px rgba(255, 69, 0, 0.5);
  transform: scale(1.05);
}

.forgot-password-btn:active {
  transform: scale(0.98);
}

.terms-link {
  color: #FFD700;
  text-decoration: none;
  font-size: 0.9rem;
  transition: all 0.3s ease;
  text-shadow: 0 0 10px rgba(255, 215, 0, 0.3);
}

.terms-link:hover {
  color: #FF4500;
  text-decoration: underline;
  text-shadow: 0 0 15px rgba(255, 69, 0, 0.5);
}

.auth-btn {
  background: linear-gradient(135deg, #FFD700 0%, #FF4500 50%, #8B4513 100%);
  background-size: 200% 200%;
  color: #000000;
  border: 1px solid rgba(255, 215, 0, 0.3);
  padding: 1rem 1.5rem;
  border-radius: 12px;
  font-size: 1rem;
  font-weight: 700;
  cursor: pointer;
  transition: all 0.4s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
  margin-top: 1rem;
  text-transform: uppercase;
  letter-spacing: 1px;
  position: relative;
  overflow: hidden;
  box-shadow: 
    0 4px 15px rgba(255, 215, 0, 0.3),
    inset 0 1px 0 rgba(255, 255, 255, 0.2);
}

.auth-btn::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.3), transparent);
  transition: left 0.5s;
}

.auth-btn:hover:not(:disabled) {
  background-position: 100% 100%;
  transform: translateY(-2px);
  box-shadow: 
    0 8px 25px rgba(255, 215, 0, 0.5),
    inset 0 1px 0 rgba(255, 255, 255, 0.3);
}

.auth-btn:hover:not(:disabled)::before {
  left: 100%;
}

.auth-btn:active:not(:disabled) {
  transform: translateY(-1px);
}

.auth-btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
  transform: none;
  background: rgba(255, 215, 0, 0.3);
}

/* Mensajes */
.message {
  padding: 1rem 1.2rem;
  border-radius: 12px;
  font-size: 0.9rem;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin-top: 1.5rem;
  backdrop-filter: blur(10px);
  border: 1px solid;
}

.error-message {
  background: rgba(220, 38, 38, 0.2);
  border-color: rgba(220, 38, 38, 0.4);
  color: #FCA5A5;
}

.success-message {
  background: rgba(34, 197, 94, 0.2);
  border-color: rgba(34, 197, 94, 0.4);
  color: #86EFAC;
}

.message-icon {
  font-size: 1.2rem;
  filter: drop-shadow(0 0 5px currentColor);
}

/* Responsive Design */
@media (max-width: 768px) {
  .nav-buttons {
    top: 1rem;
    left: 1rem;
    gap: 0.5rem;
  }
  
  .nav-btn {
    padding: 0.6rem 1rem;
    font-size: 0.8rem;
  }
  
  .nav-btn svg {
    width: 16px;
    height: 16px;
  }

  .modal-card {
    width: 95%;
    margin: 1rem;
  }

  .modal-content {
    padding: 1.5rem;
  }

  .modal-header {
    padding: 1.5rem 1.5rem 1rem;
  }

  .modal-header h3 {
    font-size: 1.4rem;
  }

  .code-input-container {
    gap: 0.5rem;
  }

  .code-digit {
    width: 40px !important;
    height: 50px;
    font-size: 1.2rem !important;
  }

  .auth-card {
    max-width: 95%;
  }

  .auth-view {
    padding: 2rem 1.5rem;
  }

  .form-options {
    flex-direction: column;
    gap: 1rem;
    align-items: flex-start;
  }
}

@media (max-width: 480px) {
  .auth-container {
    padding: 1rem;
  }

  .nav-buttons {
    top: 0.5rem;
    left: 0.5rem;
    flex-direction: column;
    gap: 0.5rem;
  }
  
  .nav-btn {
    padding: 0.5rem 0.8rem;
    border-radius: 25px;
    justify-content: center;
    min-width: 60px;
  }
  
  .nav-btn span {
    display: none; /* Ocultar texto en m√≥vil */
  }

  .modal-card {
    width: 98%;
    margin: 0.5rem;
    border-radius: 15px;
  }

  .modal-content {
    padding: 1rem;
  }

  .modal-header {
    padding: 1rem 1rem 0.5rem;
  }

  .modal-header h3 {
    font-size: 1.2rem;
  }

  .info-icon {
    font-size: 3rem !important;
  }

  .code-input-container {
    gap: 0.3rem;
  }

  .code-digit {
    width: 35px !important;
    height: 45px;
    font-size: 1.1rem !important;
  }

  .password-strength {
    flex-direction: column;
    align-items: stretch;
    gap: 0.5rem;
  }

  .strength-text {
    text-align: left;
    min-width: auto;
  }

  .auth-card {
    max-width: 100%;
  }

  .auth-view {
    padding: 2rem 1.5rem;
  }

  .auth-header h2 {
    font-size: 1.6rem;
  }

  .tab-btn {
    padding: 1rem;
    font-size: 0.85rem;
  }
}

/* Efectos adicionales para el tema de bar */
@keyframes glow {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.7; }
}

.auth-card {
  animation: subtle-glow 4s ease-in-out infinite;
}

@keyframes subtle-glow {
  0%, 100% { 
    box-shadow: 
      0 25px 50px rgba(0, 0, 0, 0.7),
      inset 0 1px 0 rgba(255, 215, 0, 0.1),
      0 0 0 1px rgba(255, 215, 0, 0.05);
  }
  50% { 
    box-shadow: 
      0 25px 50px rgba(0, 0, 0, 0.7),
      inset 0 1px 0 rgba(255, 215, 0, 0.2),
      0 0 0 1px rgba(255, 215, 0, 0.1),
      0 0 20px rgba(255, 215, 0, 0.1);
  }
}
/* ========================================= */
/* ESTILOS DEL PANEL DE ADMINISTRACI√ìN      */
/* ========================================= */

.admin-auth-view {
