  height: 2px;
  background: linear-gradient(90deg, transparent, #DC2626, transparent);
  animation: slide-glow 2s ease-in-out infinite;
}

@keyframes slide-glow {
  0%, 100% { width: 60px; opacity: 1; }
  50% { width: 100px; opacity: 0.7; }
}

.admin-auth-view .auth-header h2 {
  margin: 0 0 0.8rem 0;
  color: #DC2626;
  font-size: 2.2rem;
  font-weight: 800;
  text-shadow: 
    0 0 20px rgba(220, 38, 38, 0.6),
    0 0 40px rgba(220, 38, 38, 0.3);
  letter-spacing: 1.5px;
  text-transform: uppercase;
  background: linear-gradient(135deg, #DC2626, #EF4444, #DC2626);
  background-size: 200% 200%;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  animation: gradient-shift 3s ease infinite;
}

@keyframes gradient-shift {
  0%, 100% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
}

.admin-auth-view .auth-header p {
  margin: 0;
  color: rgba(255, 255, 255, 0.7);
  font-size: 1rem;
  font-style: italic;
  font-weight: 500;
  letter-spacing: 0.5px;
}

/* Formulario administrativo */
.admin-auth-view .auth-form {
  display: flex;
  flex-direction: column;
  gap: 1.8rem;
}

.admin-auth-view .form-group {
  display: flex;
  flex-direction: column;
  gap: 0.6rem;
  position: relative;
}

/* Labels con estilo administrativo */
.admin-auth-view .form-group label {
  color: #DC2626;
  font-weight: 700;
  font-size: 0.95rem;
  text-transform: uppercase;
  letter-spacing: 1px;
  display: flex;
  align-items: center;
  gap: 0.5rem;
